label("$P_4$", P[4], dir(P[4]));
label("$P'_1$", Pp[1], dir(Pp[1]));
label("$P'_2$", Pp[2], dir(Pp[2]));
label("$P'_3$", Pp[3], dir(-100));
label("$P'_4$", Pp[4], dir(Pp[4]));
[/asy]

